import React, { Component } from 'react';
import PropTypes from 'prop-types';
import '../css/Movies.css';

class Movie extends Component {
    // eslint-disable-next-line no-useless-constructor
    constructor() {
        super();
    }

    render() {
        return (
            <div className="col-sm-6">
                <div className="card card-view">
                    <div className="card-body">
                        <h5 className="card-title">{this.props.title}</h5>
                        <p>{this.props.description}</p>
                        <table>
                            <tr>
                            <td>Genre: <b>{this.props.genre}</b></td><td>Year: <b>{this.props.year}</b></td>
                            </tr>
                        </table>
                        <div className="center">
                            <button className="btn btn-info">Edit</button>
                            <button className="btn btn-danger">Delete</button>
                        </div>
                    </div>
                </div>
            </div>
        );
    }
}

Movie.defaultProps = {
    title: "Movie Title",
    description: "This space is for a paragraph size Description of the movie that will take up to 95% of the width of the card and will display as justified text.",
    year: "2000",
    genre: "Adventure"
};

Movie.propTypes = {
    title: PropTypes.string.isRequired,
    description: PropTypes.string.isRequired,
    year: PropTypes.number.isRequired,
    genre: PropTypes.string
};

export default Movie;
